mapper.select:
select

from land_low_use.land_info


        and id = #{id}


        and create_time = #{createTime}


        and create_user = #{createUser}


        and create_user_name = #{createUserName}


        and update_time = #{updateTime}


        and update_user = #{updateUser}


        and update_user_name = #{updateUserName}


        and land_type = #{landType}


        and category = #{category}


        and ownership = #{ownership}


        and plan_compliance = #{planCompliance}


        and use_strength = #{useStrength}


        and benefit = #{benefit}


        and desire = #{desire}


        and is_delete = #{isDelete}


        and land_area = #{landArea}

select
from land_low_use.land_info where is_delete=0
and category = #{main.category}
